        <input type="text" name="AssignedTo" className="slds-input" value={task.AssignedTo} onChange={onChange} />
      </div>
    </div>
    <div className="slds-form-element">
      <label className="slds-form-element__label" htmlFor="AssignedTo">Assigned To</label>
      <div className="slds-form-element__control">
        <input type="text" name="AssignedTo" className="slds-input" value={task.AssignedTo} onChange={onChange} />
        <input type="text" name="AssignedTo" className="slds-input" value={task.AssignedTo} onChange={onChange} />
      </div>
    </div>
    <div className="slds-form-element">
      <label className="slds-form-element__label" htmlFor="AssignedTo">Assigned To</label>
      <div className="slds-form-element__control">
        <input type="text" name="AssignedTo" className="slds-input" value={task.AssignedTo} onChange={onChange} />
        <input type="text" name="AssignedTo" className="slds-input" value={task.AssignedTo} onChange={onChange} />
      </div>
    </div>
    {/* Add other fields similarly */}
    {/* For example, for Status: */}
    <div className="slds-form-element">
      <label className="slds-form-element__label" htmlFor="Status">Status</label>
      <div className="slds-form-element__control">
        <input type="text" name="Status" className="slds-input" value={task.Status} onChange={onChange} />
      </div>
    </div>
    {/* Continue for other fields like DueDate, Priority, Comments, Description */}
  </div>
);

export default Tasks;
