import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '@salesforce-ux/design-system/assets/styles/salesforce-lightning-design-system.min.css';
import './Tasks.css'; // Import your CSS file

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/tasks/all');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleRefresh = () => {
    fetchTasks();
  };

  return (
    <div className="slds-scope"> {/* This is the container that will be centered */}
      <div className="slds-page-header">
        <div className="slds-grid">
          <div className="slds-col slds-has-flexi-truncate">
            <p className="slds-text-heading_medium">Tasks</p>
            <p>All Tasks</p>
          </div>
          <div className="slds-col slds-no-flex slds-grid slds-align-top">
            <div className="slds-col">
              <button className="slds-button slds-button_brand" onClick={() => alert('Add new task')}>New Task</button>
              <button className="slds-button slds-button_neutral" onClick={handleRefresh}>Refresh</button>
            </div>
            <div className="slds-col">
              <div className="slds-form-element">
                <div className="slds-form-element__control">
                  <input type="text" className="slds-input" placeholder="Search" value={searchTerm} onChange={handleSearchChange} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <table className="slds-table slds-table_cell-buffer slds-table_bordered slds-table_fixed-layout">
        <thead>
          <tr className="slds-line-height_reset">
            <th className="" scope="col"><div className="slds-truncate" title="Assigned To">Assigned To</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Status">Status</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Due Date">Due Date</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Priority">Priority</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Comments">Comments</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Description">Description</div></th>
            <th className="" scope="col"><div className="slds-truncate" title="Actions">Actions</div></th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task._id}>
              <td data-label="Assigned To">{task.AssignedTo}</td>
              <td data-label="Status">{task.Status}</td>
              <td data-label="Due Date">{new Date(task.DueDate).toLocaleDateString()}</td>
              <td data-label="Priority">{task.Priority}</td>
              <td data-label="Comments">{task.Comments}</td>
              <td data-label="Description">{task.Description}</td>
              <td data-label="Actions">
                <button className="slds-button slds-button_neutral">Edit</button>
                <button className="slds-button slds-button_destructive">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Tasks;
