import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/tasks/all');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleRefresh = () => {
    fetchTasks();
  };

  return (
    <div className="container">
      <div className="my-4">
        <h2>Tasks</h2>
        <p>All Tasks</p>
        <div className="d-flex justify-content-between mb-3">
          <div>
            <button className="btn btn-primary" onClick={() => alert('Add new task')}>New Task</button>
            <button className="btn btn-secondary ms-2" onClick={handleRefresh}>Refresh</button>
          </div>
          <input
            type="text"
            className="form-control w-25"
            placeholder="Search"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </div>
      <table className="table table-bordered">
        <thead className="table-light">
          <tr>
            <th scope="col">Assigned To</th>
            <th scope="col">Status</th>
            <th scope="col">Due Date</th>
            <th scope="col">Priority</th>
            <th scope="col">Comments</th>
            <th scope="col">Description</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task._id}>
              <td>{task.AssignedTo}</td>
              <td>{task.Status}</td>
              <td>{new Date(task.DueDate).toLocaleDateString()}</td>
              <td>{task.Priority}</td>
              <td>{task.Comments}</td>
              <td>{task.Description}</td>
              <td>
                <button className="btn btn-outline-secondary me-1">Edit</button>
                <button className="btn btn-outline-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Tasks;
