import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '@salesforce-ux/design-system/assets/styles/salesforce-lightning-design-system.min.css';
// import './Tasks.css'; 

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/tasks/all');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleRefresh = () => {
    fetchTasks();
  };

  return (
    <div className="slds-scope">
      {/* Container */}
      <div className="slds-grid slds-grid_vertical-align-center slds-m-around_medium">
        {/* Row */}
        <div className="slds-col slds-size_1-of-1">
          <div className="slds-box slds-box_xx-small">Full Width</div>
        </div>

        {/* Row */}
        <div className="slds-col slds-size_1-of-2">
          <div className="slds-box slds-box_xx-small">50% Width</div>
        </div>
        <div className="slds-col slds-size_1-of-2">
          <div className="slds-box slds-box_xx-small">50% Width</div>
        </div>

        {/* Row */}
        <div className="slds-col slds-size_1-of-3">
          <div className="slds-box slds-box_xx-small">33% Width</div>
        </div>
        <div className="slds-col slds-size_1-of-3">
          <div className="slds-box slds-box_xx-small">33% Width</div>
        </div>
        <div className="slds-col slds-size_1-of-3">
          <div className="slds-box slds-box_xx-small">33% Width</div>
        </div>
      </div>
    </div>
  );
};

export default Tasks;
