import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '@salesforce-ux/design-system/assets/styles/salesforce-lightning-design-system.min.css';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/tasks/all');
      setTasks(response.data);
      console.log(tasks);

    } catch (error) {
      console.error('Failed to fetch tasks:', error);
    }
  };

  return (
    <div className="slds-scope">
      <div className="slds-table_header-fixed_container">
        <table className="slds-table slds-table_cell-buffer slds-table_bordered slds-table_fixed-layout">
          <thead>
            <tr className="slds-line-height_reset">
              <th className="" scope="col">
                <div className="slds-truncate" title="Assigned To">Assigned To</div>
              </th>
              <th className="" scope="col">
                <div className="slds-truncate" title="Status">Status</div>
              </th>
              <th className="" scope="col">
                <div className="slds-truncate" title="Due Date">Due Date</div>
              </th>
              <th className="" scope="col">
                <div className="slds-truncate" title="Priority">Priority</div>
              </th>
              <th className="" scope="col">
                <div className="slds-truncate" title="Comments">Comments</div>
              </th>
              <th className="" scope="col">
                <div className="slds-truncate" title="Actions">Actions</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td data-label="Assigned To">{task.assignedTo}</td>
                <td data-label="Status">{task.status}</td>
                <td data-label="Due Date">{task.dueDate}</td>
                <td data-label="Priority">{task.priority}</td>
                <td data-label="Comments">{task.comments}</td>
                <td data-label="Actions">
                  <button className="slds-button slds-button_neutral">Edit</button>
                  <button className="slds-button slds-button_destructive">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Tasks;
