import React, { useState, useEffect } from "react";
import axios from "axios";

const Tasks = () => {
    const [tasks, setTasks] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
  
    useEffect(() => {
      fetchTasks();
    }, []);
  
    const fetchTasks = async () => {
      try {
        const response = await axios.get("http://localhost:8000/tasks/all");
        setTasks(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };
  
    const handleSearchChange = (event) => {
      setSearchTerm(event.target.value);
    };
  
    const handleRefresh = () => {
      fetchTasks();
    };
  
    return (
      <div className="container mt-5">
        {/* <div className="row"> */}
          <table className="table table-bordered">
            <thead className="table-light">
              <tr>
                <th scope="col">Assigned To</th>
                <th scope="col">Status</th>
                <th scope="col">Due Date</th>
                <th scope="col">Priority</th>
                <th scope="col">Comments</th>
                <th scope="col">Description</th>
                <th scope="col">Actions</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task._id}>
                  <td className="border">{task.AssignedTo}</td>
                  <td className="border">{task.Status}</td>
                  <td className="border">{new Date(task.DueDate).toLocaleDateString()}</td>
                  <td className="border">{task.Priority}</td>
                  <td className="border">{task.Comments}</td>
                  <td className="border">{task.Description}</td>
                  <td>
                    <button className="btn btn-outline-secondary me-1">
                      Edit
                    </button>
                    <button className="btn btn-outline-danger">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        {/* </div> */}
      </div>
    );
  };
  
  export default Tasks;
